#Day 1 Part 1
def calcFloor(): #Tracks the floor Santa is on
    floor = 0
    for i in floorString:
        if(i == "("):
            floor += 1
        else:
            floor -= 1
    return floor

#Day 1 Part 2
#Simple function to calculate the floor Santa is on! - Modified to calculate position when Santa is in the basement!
def calcBasement():
    floor = 0
    pos = 1
    # print(len(floorString))

    for i in floorString:
        if(floor == -1):
            return pos-1
            break
        if(i == "("):
            floor += 1
        else:
            floor -= 1
        pos += 1
    return -1

print(calcFloor())
print(calcBasement())